import React, { Component } from "react";
import {Form,Container, Card,Row,Col , Button,FormControl} from "react-bootstrap";

class Post extends Component {

    constructor(props){
        super(props);
        this.state  = {
            publisherMessage: "",
            messages: []
        }
        this.textInput = React.createRef();
        this.postMessage = this.postMessage.bind(this);

    }
    postMessage(){
        if(this.state.publisherMessage.trim() === ""){
            console.log('blank')
        }else{
            let msg = this.state.publisherMessage;
            this.setState({
                messages: this.state.messages.concat(msg)
            })
            this.textInput.current.value = ""
            this.handleChange()
        }
    }
    componentDidUpdate(prevProp,prevState){
        if(prevState.messages.length < this.state.messages.length){
            console.log(this.state.messages)
        }
    }

    handleChange() {
        this.setState({
            publisherMessage: this.textInput.current.value
        })
     }
    render(){
        return(

            <Container>
                <br />
              <Card>
                <Card.Body>
                <Form>

                    <Card style={{ height: '500px' }}>
                    <Card.Body>
                        Hello
                    </Card.Body>
                    </Card>
                    <br />
                    <br />
                <Row>
                <Col sm={11}>
                  <Form.Group controlId="exampleForm.ControlTextarea1">

                    <FormControl ref={this.textInput} type="text" onChange={() => this.handleChange()} />
                  </Form.Group>
                </Col>
                <Col sm={1}><Button variant="success"    
                onClick={this.postMessage.bind(this)}>Post</Button></Col>
              </Row>
              </Form>
              </Card.Body>
              </Card>
            </Container>

        );
    }
}

export default Post;
