import React, { Component } from "react";
import {Form,Container, Card,Row,Col , Button } from "react-bootstrap";

class Post extends Component {

    constructor(props){
        super(props);
        this.textInput = React.createRef(); 
    }
    postMessage(){
        console.log("--");
    }
    handleChange() {
        const value = this.textInput.current.value;
        console.log(value);
     }
    render(){
        return(

            <Container>
              <Card>
                <Card.Body>
                <Form>
                <Row>
                <Col sm={11}>
                  <Form.Group controlId="exampleForm.ControlTextarea1">

                    <FormControl ref={this.textInput} type="text" onChange={() => this.handleChange()} />
                  </Form.Group>
                </Col>
                <Col sm={1}><Button variant="success"    
                onClick={this.postMessage}>Post</Button></Col>
              </Row>
              </Form>
              </Card.Body>
              </Card>
            </Container>

        );
    }
}

export default Post;
